}
hr {
  position: relative;
  margin: 2rem auto;
  border: 2px dashed var(--hr-border);
}
.table-wrap {
  overflow-x: scroll;
  margin: 0 0 1rem;
}
table {
  display: table;
  width: 100%;
  border-spacing: 0;
  border-collapse: collapse;
  empty-cells: show;
}
