  color: #f7f7f7;
}
button {
  padding: 0;
  outline: 0;
  border: none;
  background: none;
  cursor: pointer;
}
a {
  color: #99a9bf;
  text-decoration: none;
  word-wrap: break-word;
  transition: all 0.2s;
  overflow-wrap: break-word;
}
a:hover {
  color: #49b1f5;
}
.is-center {
  text-align: center;
}
.copy-true {
  user-select: all;
}
.pull-left {
  float: left;
}
.pull-right {
  float: right;
}
.button--animated {
  position: relative;
  z-index: 1;
  transition: color 1s;
}
.button--animated:before {
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  z-index: -1;
  background: var(--btn-hover-color);
  content: '';
  transition: transform 0.5s ease-out;
  transform: scaleX(0);
  transform-origin: 0 50%;
}
.button--animated:hover:before {
  transition-timing-function: cubic-bezier(0.45, 1.64, 0.47, 0.66);
  transform: scaleX(1);
}
img {
  max-width: 100%;
